package com.nivida.bossb2b.Model;import com.nivida.bossb2b.Web;import retrofit2.Call;import retrofit2.http.Field;import retrofit2.http.FormUrlEncoded;import retrofit2.http.POST;/** * Created by NWSPL-17 on 21-Apr-17. */public interface APIServices {    @FormUrlEncoded    @POST(Web.COMPANY_SALESPERSON)    Call<VendorDataList> companySalesPerson(@Field("com_sales_per_id") String salesPersonID);    @FormUrlEncoded    @POST(Web.DISTRIBUTOR_SALESPERSON)    Call<VendorDataList> distSalesPersonList(@Field("sales_per_id") String salesPersonID);}