package com.nivida.bossb2b.Model;import com.google.gson.Gson;import com.google.gson.GsonBuilder;import com.nivida.bossb2b.Web;import retrofit2.Retrofit;import retrofit2.converter.gson.GsonConverterFactory;/** * Created by NWSPL-17 on 21-Apr-17. */public class ServiceGenerator {    public static APIServices getServiceClass() {        Gson gson = new GsonBuilder()                .setLenient()                .create();        Retrofit retrofit = new Retrofit.Builder().baseUrl(Web.LINK)                .addConverterFactory(GsonConverterFactory.create(gson))                .addConverterFactory(StringConverterFactory.create())                .build();        return retrofit.create(APIServices.class);    }}